cS = -1;
cT = -1;
sorted_color = "2C497F"

options = [true, false, false, true, false, false]

function monkey(){
	document.getElementById('monkey_img').src = "imgs/" + files[Math.floor(Math.random() * (files.length))];
}

window.onload = monkey();

function main(){

	monkey()

	if (options[0] == true){
		sorted_color = colors[Math.floor(Math.random() * (colors.length))]
		document.getElementsByTagName('body')[0].style.backgroundColor = "#" + sorted_color;

	} else if (options[1] == true){
		sorted_color = colors[cS];

	} else if (options[2] == true){
		sorted_color = document.getElementById("color_custom").value;
	}

	if (options[3] == true){
		document.getElementById("text").innerHTML = frases[Math.floor(Math.random() * (frases.length))];

	} else if (options[4] == true){
		document.getElementById("text").innerHTML = document.getElementById("cT").value;
	} else if (options[5] == true){
		document.getElementById("text").innerHTML = document.getElementById("frase_custom").value;
	}

}

function change_solid(){
	cS += 1;
	document.getElementById("cS").style.backgroundColor = "#" + colors[cS];
	sorted_color = colors[cS];

	if (cS == colors.length - 1){
		cS = -1;
	}
}

function change_text(){
	cT += 1;
	document.getElementById("cT").value = frases[cT];

	if (cT == frases.length - 1){
		cT = -1;
	}
}


function check(num){

	if (num < 3){
		for (c = 0; c < 3; c++){options[c] = false}
		options[num] = true;

		for (c = 0; c < 3; c++){
			document.getElementsByClassName("checkboxS")[c].checked = false;
		}
	} else{
		for (c = 3; c < 6; c++){options[c] = false}
		options[num] = true;

		for (c = 3; c < 6; c++){
			document.getElementsByClassName("checkboxS")[c].checked = false;
		}
	}

    document.getElementsByClassName("checkboxS")[num].checked = true;
}


function open_menu(){
	document.getElementsByTagName('body')[0].style.backgroundColor = "#1F363D";
	document.getElementById('menu').style.display = "inherit";
}

function close_menu(){
	document.getElementById('menu').style.display = "none";

	if (options[2] == true){
		document.getElementsByTagName('body')[0].style.backgroundColor = "#" + document.getElementById("color_custom").value;;
	} else{
		document.getElementsByTagName('body')[0].style.backgroundColor = "#" + sorted_color;
	}
}